package com.example.trying3.dao;

import com.example.trying3.config.DatabaseConnection;
import java.sql.*;
import java.util.*;

/**
 * Data Access Object untuk Pelanggan.
 * Mengelola operasi CRUD dan ekspor data pelanggan.
 */
public class PelangganDAO {
    public List<Map<String, String>> getAllPelangganForExport() {
        List<Map<String, String>> list = new ArrayList<>();
        String sql = "SELECT nama, email, no_telepon, alamat FROM pelanggan";

        try (Connection conn = DatabaseConnection.getConnection();
             Statement st = conn.createStatement();
             ResultSet rs = st.executeQuery(sql)) {

            while (rs.next()) {
                Map<String, String> map = new HashMap<>();
                map.put("Nama", rs.getString("nama"));
                map.put("Email", rs.getString("email"));
                map.put("Telepon", rs.getString("no_telepon"));
                map.put("Alamat", rs.getString("alamat"));
                list.add(map);
            }
        } catch (SQLException e) {
            e.printStackTrace();
        }
        return list;
    }
}

package com.example.trying3.dao;

import com.example.trying3.config.DatabaseConnection;
import java.sql.*;
import java.util.*;

/**
 * Data Access Object untuk Pelanggan.
 * Mengelola operasi CRUD dan ekspor data pelanggan.
 */
public class PelangganDAO {
    public List<Map<String, String>> getAllPelangganForExport() {
        List<Map<String, String>> list = new ArrayList<>();
        String sql = "SELECT nama, email, no_telepon, alamat FROM pelanggan";

        try (Connection conn = DatabaseConnection.getConnection();
             Statement st = conn.createStatement();
             ResultSet rs = st.executeQuery(sql)) {

            while (rs.next()) {
                Map<String, String> map = new HashMap<>();
                map.put("Nama", rs.getString("nama"));
                map.put("Email", rs.getString("email"));
                map.put("Telepon", rs.getString("no_telepon"));
                map.put("Alamat", rs.getString("alamat"));
                list.add(map);
            }
        } catch (SQLException e) {
            e.printStackTrace();
        }
        return list;
    }
}

package com.example.trying3.model;

import java.time.LocalDateTime;

/**
 * Model untuk User/Pengguna sistem.
 * Menyimpan informasi user, credentials, dan security features.
 */
public class User {
    private int idUser;
    private String username;
    private String passwordHash;
    private String namaLengkap;
    private String email;
    private int idRole;
    private boolean isActive;
    private LocalDateTime lastLogin;
    private int failedLoginAttempts;
    private LocalDateTime lockedUntil;
    private LocalDateTime createdAt;
    private LocalDateTime updatedAt;

    public User() {}

    public User(int idUser, String username, String namaLengkap, String email,
                int idRole, boolean isActive) {
        this.idUser = idUser;
        this.username = username;
        this.namaLengkap = namaLengkap;
        this.email = email;
        this.idRole = idRole;
        this.isActive = isActive;
    }

    // ...existing code...
    public int getIdUser() { return idUser; }
    public void setIdUser(int idUser) { this.idUser = idUser; }

    public String getUsername() { return username; }
    public void setUsername(String username) { this.username = username; }

    public String getPasswordHash() { return passwordHash; }
    public void setPasswordHash(String passwordHash) { this.passwordHash = passwordHash; }

    public String getNamaLengkap() { return namaLengkap; }
    public void setNamaLengkap(String namaLengkap) { this.namaLengkap = namaLengkap; }

    public String getEmail() { return email; }
    public void setEmail(String email) { this.email = email; }

    public int getIdRole() { return idRole; }
    public void setIdRole(int idRole) { this.idRole = idRole; }

    public boolean isActive() { return isActive; }
    public void setActive(boolean active) { isActive = active; }

    public LocalDateTime getLastLogin() { return lastLogin; }
    public void setLastLogin(LocalDateTime lastLogin) { this.lastLogin = lastLogin; }

    public int getFailedLoginAttempts() { return failedLoginAttempts; }
    public void setFailedLoginAttempts(int failedLoginAttempts) {
        this.failedLoginAttempts = failedLoginAttempts;
    }

    public LocalDateTime getLockedUntil() { return lockedUntil; }
    public void setLockedUntil(LocalDateTime lockedUntil) { this.lockedUntil = lockedUntil; }

    public LocalDateTime getCreatedAt() { return createdAt; }
    public void setCreatedAt(LocalDateTime createdAt) { this.createdAt = createdAt; }

    public LocalDateTime getUpdatedAt() { return updatedAt; }
    public void setUpdatedAt(LocalDateTime updatedAt) { this.updatedAt = updatedAt; }

    /**
     * Cek apakah akun terkunci berdasarkan waktu locked_until.
     */
    public boolean isLocked() {
        if (lockedUntil == null) return false;
        return LocalDateTime.now().isBefore(lockedUntil);
    }

    /**
     * Increment failed attempts dan lock akun jika mencapai 5 kali gagal.
     */
    public void incrementFailedAttempts() {
        this.failedLoginAttempts++;
        if (this.failedLoginAttempts >= 5) {
            this.lockedUntil = LocalDateTime.now().plusMinutes(30);
        }
    }

    public void resetFailedAttempts() {
        this.failedLoginAttempts = 0;
        this.lockedUntil = null;
    }

    @Override
    public String toString() {
        return "User{" +
                "id=" + idUser +
                ", username='" + username + '\'' +
                ", namaLengkap='" + namaLengkap + '\'' +
                ", email='" + email + '\'' +
                ", role=" + idRole +
                ", active=" + isActive +
                '}';
    }
}

